con:testSuite.con:tearDownScript:
// Log teardown information
log.info("Suite Level Teardown script: Execution Start")

// Import required classees
import java.util.concurrent.TimeUnit

// Declare and initialize script variables
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def dataFolder = groovyUtils.projectPath
def failedTestCases = 0
def succeedTestCases = 0
def totalTestCases = 0
def failedTestCaseTable = ""
def passedTestCaseTable = ""
def tempResultThread = ""
def counter=0
def execStatus = 0


/*
 * Create pass/fail results html tables
 */
// Create failed test cases table
failedTestCaseTable += "<br><table id='failedTestCases' align='center' class='failedTable'><tr><thead><th colspan=4 bgcolor='#FF0000'><h2>"
failedTestCaseTable += runner.testSuite.name.toString()+" - Failures"+"</h2></th></thead></tr><tr><thead><th style='border: 2px solid black;width:5%'><h3>S.No.</h3></th>" 
failedTestCaseTable += "<th style='border: 2px solid black;width:15%'><h3>Test Case Name</h3></th><th style='border: 2px solid black;width:25%'><h3>Test Step</h3></th>"
failedTestCaseTable += "<th style='border: 2px solid black;width:55%;'><h3>Error Message</h3></th></thead></tr>"

// Create passed test cases table
passedTestCaseTable += "<br><table id='passedTestCases' class='successTable' align='center'><tr><thead><th colspan=3 bgcolor='#44BB00' align='center'><h2>"
passedTestCaseTable += runner.testSuite.name.toString()+" - Success"+"</h2></th></thead></tr><tr bgcolor='#C0C0C0'><thead><th style='width:10%'><h3>S.No.</h3></th>"
passedTestCaseTable += "<th style='width:20%'><h3>Test Case Name</h3></th><th style='width:70%'><h3>Test Case Description</h3></th></thead></tr>"



/*
 * Create new file to store execution report
 */
// Fetch directory for the report[ProductVersion/SuiteName/ResultFile]
RootResultFolder = runner.testSuite.project.getPropertyValue("resultFolder")

// Create file
def emailFileName = runner.testSuite.name.toString() + ".html"
def absoluteEmailFilePath = RootResultFolder + "\\" + emailFileName
Runtime.getRuntime().exec("cmd /c touch "+absoluteEmailFilePath)
def testRunReport = new File(absoluteEmailFilePath)


/*
 * Check duplicate file presence else fetch and store results in respective passed/failedTestcaseTable(s)
 */
// Verify if file already exists
if(testRunReport.exists())
{
 	//log.error("Error a file named " + fileName + "already exists")
}
else
{
	// Create a copy of 'runner' to make modifications and sort testcase results
	def array=runner.results
	int i, j;
   	for (i = 0; i < array.size(); i++){  
     	for (j = 0; j < array.size()-i-1; j++){
     		k=j+1
     		def sub1=array[j].testCase.name.split('TC')[1]
     		def sub2=array[k].testCase.name.split('TC')[1]
          	if (sub1>sub2){
          		array.swap(j,k)
          	}
     	}
   	}

   	// Iterate through all results and create result tables
	array.each { testCaseResult ->
		def name = testCaseResult.testCase.name
		if(!name.contains("TC00")){
			def description = testCaseResult.testCase.description.toString().split("\\n")[0].trim()
			def failedTestCaseCounter = 0
			totalTestCases++
			if(testCaseResult.status.toString().contains('FAIL')){
				failedTestCases++
				counter=0
				int asciiCounter=96
				tempResultThread = ""
				testCaseResult.getResults().each{ testStepResult ->
					def stepName=testStepResult.getTestStep().getName()
					testStepResult.messages.each() { msg ->	
						++counter
						++asciiCounter
						if(msg.length()>99) {
							def msgArray = msg.split("(?<=\\G.{100})")
							msg = ""
							for(int index=0;index<msgArray.size();index++){
								msg+=msgArray[index]+"<br>"
							}
						}
						tempResultThread += "<tr style='text-align:left;'><td>"+String.valueOf(Character.toChars(asciiCounter))+". "+stepName+"</td><td>"+msg+"</td></tr>"
					}		 
				}
				counter=counter+1
				failedTestCaseTable += "<tr style='border: 2px solid black;'><td rowspan='"+(counter)+"' style='text-align:center;'>$failedTestCases</td><th rowspan='"+(counter)+"'>$name</th>"
				failedTestCaseTable += "<th colspan=2 style='border: 2px solid black;background-color:#E89999;text-align:left;padding: 10px 0px 10px 5px;'>$description</th>" + tempResultThread
			}
			else
			{
				succeedTestCases++
				passedTestCaseTable = passedTestCaseTable + "<tr><td>$succeedTestCases</td><th>$name</th><th align=left>$description</th></tr>"
			}
		}
	}
}

// Close html tags of tables
failedTestCaseTable=failedTestCaseTable+"</table><br>"
passedTestCaseTable=passedTestCaseTable+"</table><br>"


/* 
 *  Build consolidated html report
 */
 // Create suite execution summary table
def summaryTable = "<table id='summaryTestCases' align='center' class='summaryTable'><tr><thead><th bgcolor='#B0B0B0' colspan=2><h2>"
summaryTable += runner.testSuite.name.toString()+" - Summary</h2></th></thead></tr><tr><th><h3>Total TCs</h3></th>"
summaryTable += "<td>$totalTestCases</td></tr><tr><th><h3>Passed TCs</h3></th>"
summaryTable += "<td bgcolor='#44BB00'>$succeedTestCases</td></tr><tr><th>"
summaryTable += "<h3>Failed TCs</h3></th><td bgcolor='#FF0000'>$failedTestCases</td></tr></table>"

// Consoidate relevant tables in one string variable
def finalReport=""

if(failedTestCases!=0 && succeedTestCases==0){
	finalReport=summaryTable+failedTestCaseTable;
	execStatus=1;
	testSuite.setPropertyValue("execStatus" , execStatus.toString())

}
if (failedTestCases==0 && succeedTestCases!=0){
	finalReport=summaryTable+passedTestCaseTable;
	execStatus=0;
	testSuite.setPropertyValue("execStatus" , execStatus.toString())

}
if (failedTestCases!=0 && succeedTestCases!=0){
	finalReport=summaryTable+failedTestCaseTable+passedTestCaseTable;
	execStatus=1;
	testSuite.setPropertyValue("execStatus" , execStatus.toString())

}
if (failedTestCases==0 && succeedTestCases==0){
	finalReport = summaryTable;
	execStatus=1;
	testSuite.setPropertyValue("execStatus" , execStatus.toString())
}

// Write results in emailable report attachment
testRunReport.write finalReport




// Attach execution summary to consolidated execution summary table
def consolidatedSummary = "<tr><td>"+runner.testSuite.name.toString()+"</td><td>$totalTestCases</td><td bgcolor='#44BB00'>$succeedTestCases</td><td bgcolor='#FF0000'>$failedTestCases</td><td>"
consolidatedSummary += getRunDuration(runner.getTimeTaken())+"</td></tr>"
def consolidatedSummaryFilePath = RootResultFolder + "\\SummaryTable.html"
def consolidatedFile = new File(consolidatedSummaryFilePath)
consolidatedFile.append(consolidatedSummary)

/* 
 *  Verification that script has run error free
 */
// Delete start time suite property
log.info("Suite Level Teardown script: Execution Successful")


return;

/**
 * 'getRunDuration' method definition
 */
def String getRunDuration(long millis) {

	/*
	 * Convert milliseconds int fomat 'hh:mm:ss'
	 */
	// Extract hours, minutes and seconds from total milliseconds
	long hours = TimeUnit.MILLISECONDS.toHours(millis);
	long minutes = TimeUnit.MILLISECONDS.toMinutes(millis) % TimeUnit.HOURS.toMinutes(1);
	long seconds = TimeUnit.MILLISECONDS.toSeconds(millis) % TimeUnit.MINUTES.toSeconds(1);

	// Convert into required format
	String format = String.format("%02d:%02d:%02d", Math.abs(hours), Math.abs(minutes), Math.abs(seconds));

	return(format);
}

