.response.responseHeaders["X-egain-session"].get(0))

import groovy.json.JsonSlurper
import org.json.JSONObject
import org.json.XML
import groovy.util.XmlSlurper
import groovy.util.XmlParser

/*
 * Get all Issue Types from database
 */
def xml1 = testRunner.testCase.getTestStepByName("getAllIssueTypes").getPropertyValue("ResponseAsXml")
def slurped = new XmlSlurper().parseText(xml1)
def rowSize=slurped.ResultSet.Row.size()

def IssueTypeNameFromDB=new Object[rowSize]
def IssueTypeIdFromDB=new Object[rowSize]
for(int i=0;i<rowSize;i++)
{
		      IssueTypeNameFromDB[i] = slurped.ResultSet.Row[i].NAME.toString().toLowerCase()
		       IssueTypeIdFromDB[i] = slurped.ResultSet.Row[i].ISSUE_ID.toString().toLowerCase()
 }

 log.info IssueTypeIdFromDB.size()

 /*
 * Get Issue Type from API request
 */
def responseContent = testRunner.testCase.getTestStepByName("searchIssueType").getPropertyValue("response")
def slurperresponse = new JsonSlurper().parseText(responseContent)

def IssueTypeNameSize = slurperresponse.issueType.name.size()

def IssueTypeNameFromResponse=new Object[slurperresponse.issueType.name.size()]
def IssueTypeIdFromResponse=new Object[slurperresponse.issueType.id.size()]
for(int i=0;i<slurperresponse.issueType.name.size();i++)
{
	IssueTypeNameFromResponse[i] = slurperresponse.issueType[i].name.toString().toLowerCase()
	IssueTypeIdFromResponse[i] = slurperresponse.issueType[i].id.toString().toLowerCase()
}

// sort response from API in ascending order by name
IssueTypeNameFromResponse.sort(true)
IssueTypeIdFromResponse.sort(true)
IssueTypeNameFromDB.sort(true)
IssueTypeIdFromDB.sort(true)

assert rowSize==IssueTypeNameSize

assert IssueTypeIdFromDB==IssueTypeIdFromResponse

