/**
* Method to enable / disable response SLA
**/
def enabeDisableSLA(String enable)
{
	Boolean var 
	if (enable.equals("true"))
		var = false
	else
		var = true
	//Actual script that traverse thru the project -> suite -> case -> step
		runner.project.testSuiteList.each { suite ->

			suite.testCaseList.each { tCase ->

				tCase.testStepList.each { step ->

					if (step instanceof RestTestRequestStep) {

						def assertions = step.assertionList
						for (def assertion: assertions)
						{
							if (assertion instanceof ResponseSLAAssertion) {	
							assertion.disabled=var
							}
						}

					}	
				}
			}
		}
}

