/**
 * 'sendEmail' method definition
 */
def sendEmail(String dataFolder, String emailBody, String absoluteEmailFilePath){

	/*
	 *  Send email report
	 */ 
	// Get values from suite properties
	def fromAddress = runner.project.getPropertyValue("emailReportFromAddress") 
	def fromAddressPassword = runner.project.getPropertyValue("emailReportFromAddressPassword"); 
	def toAddress = runner.project.getPropertyValue("emailReportToAddress")
	def smtpHost = runner.project.getPropertyValue("emailHost")
	def subject = "Report Summary - API Results For "+runner.project.name.toString() + " - " + finalStatus1
	def jarFile=dataFolder+"\\..\\lib\\Reporting\\"+runner.project.getPropertyValue("emailReportJarFile")
	log.info subject

	// Construct and xecute command
	def command = "java -jar \""+jarFile+"\" \""+smtpHost+"\" \""+fromAddress+"\" \""+fromAddressPassword+"\" \""+toAddress+"\" \""+subject+"\" \""+emailBody+"\" \""+absoluteEmailFilePath+"\""
	def process = command.execute()
	process.waitFor()
}

/**
 * 'getRunDuration' method definition
 */
def String getRunDuration(long millis) {

	/*
	 * Convert milliseconds int fomat 'hh:mm:ss'
	 */
	// Extract hours, minutes and seconds from total milliseconds
	long hours = TimeUnit.MILLISECONDS.toHours(millis);
	long minutes = TimeUnit.MILLISECONDS.toMinutes(millis) % TimeUnit.HOURS.toMinutes(1);
	long seconds = TimeUnit.MILLISECONDS.toSeconds(millis) % TimeUnit.MINUTES.toSeconds(1);

	// Convert into required format
	String format = String.format("%02d:%02d:%02d", Math.abs(hours), Math.abs(minutes), Math.abs(seconds));

	return(format);
}
